settings.liquid:
{% if shop.password_message %}
<div style="height: 100vh; display: flex; justify-content: center; align-items: center; background-image: url(https://cdn.shopify.com/s/files/1/2303/3351/files/RYAN_PERNOFSKO-SALE-1.jpg?v=1679550234); background-size: cover; background-position: center;">
  <div style="text-align: center; font-family: 'Futura', sans-serif;">
    <!-- Countdown -->
    <div id="countdown"></div>

    <h2 style="margin-bottom: 20px;">Enter your password to continue</h2>
    <form action="/password" method="post" style="display: inline-block; margin-top: 40px;">
      <input type="password" name="password" placeholder="Enter your password" style="padding: 10px 20px; border: 2px solid black; font-size: 15px; font-family: 'Futura', sans-serif; color: #000;">
      <input type="submit" value="Enter" style="display: inline-block; margin-top: 20px; padding: 10px 20px; border: 2px solid black; text-decoration: none; font-size: 15px; font-family: 'Futura', sans-serif; color: #000; transition: all 0.2s ease-in-out;">
    </form>
    <p>{{ shop.password_message }}</p>
  </div>
</div>

<script>
function updateCountdown() {
  // Set the target end time in Sydney time zone (UTC+10:00)
  var targetTime = new Date('2023-06-09T08:00:00+10:00').getTime();

  // Calculate the time remaining
  var currentTime = new Date().getTime();
  var timeRemaining = targetTime - currentTime;

  // Ensure the countdown doesn't go below zero
  if (timeRemaining <= 0) {
    document.getElementById('countdown').innerHTML = '<div class="countdown__expired">Sale has ended.</div>';
    return;
  }

  // Convert the time remaining to hours, minutes, and seconds
  var hoursRemaining = Math.floor((timeRemaining / (1000 * 60 * 60)) % 24);
  var minutesRemaining = Math.floor((timeRemaining / (1000 * 60)) % 60);
  var secondsRemaining = Math.floor((timeRemaining / 1000) % 60);

  // Display the countdown
  var countdownElement = document.getElementById('countdown');
  countdownElement.innerHTML =
    '<div class="countdown__item">' +
    '<div class="countdown__value">' + hoursRemaining + '</div>' +
    '<div class="countdown__label">Hours</div>' +
    '</div>' +
    '<div class="countdown__item">' +
    '<div class="countdown__value">' + minutesRemaining + '</div>' +
    '<div class="countdown__label">Minutes</div>' +
    '</div>' +
    '<div class="countdown__item">' +
    '<div class="countdown__value">' + secondsRemaining + '</div>' +
    '<div class="countdown__label">Seconds</div>' +
    '</div>';

  // Update the countdown every second
  setTimeout(updateCountdown, 1000);
}

// Start the countdown
updateCountdown();
</script>

<style>
.countdown__item {
  display: inline-block;
  margin-right: 20px;
  text-align: center;
}

.countdown__value {
  font-size: 48px;
  font-weight: regular;
}

.countdown__label {
  font-size: 14px;
  margin-top: 10px;
}

.countdown__expired {
  font-size: 24px;
  font-weight: regular;
  color: red;
  margin-top: 20px;
}
</style>
{% endif %}
